import React, {useEffect , useState } from 'react'
import { PrettyChatWindow } from "react-chat-engine-pretty";
import { ChatEngineWrapper, ChatSocket, ChatFeed } from 'react-chat-engine'
import './ChatComponent.css';

const ChatComponent = () => {
    const [filterValue, setFilterValue] = useState('');

    useEffect(() => {
        const handleFilterInput = (event) => {
            setFilterValue(event.target.value.toLowerCase().trim());
        };

        document.getElementById("filterInput").addEventListener("input", handleFilterInput);

        return () => {
            document.getElementById("filterInput").removeEventListener("input", handleFilterInput);
        };
    }, []); 

    const filterChatCards = () => {
        const cards = document.querySelectorAll(".ce-chat-card");
        cards.forEach((card) => {
            const title = card.querySelector(".ce-chat-card-title").textContent.toLowerCase().trim();
            if (title.includes(filterValue)) {
                card.classList.remove('hidden');
            } else {
                card.classList.add('hidden');
            }
        });
    };

    useEffect(() => {
        filterChatCards();
    }, [filterValue]);

    return (
        <div style={{ height: '100vh', width: '100vw'}}>
            <div className="ce-chat-list">
                <input type="text" id="filterInput" placeholder="Type to filter" />
            </div>
            <PrettyChatWindow  
                offset={3} 
                height='100vh'
                width='50vh'
                username={window.username}
                secret={window.secret}
                projectId='f0e1d373-0995-4a51-a2df-cf314fc0e034'
                style={{ height: '100%'}}
            />
        </div>
    );
};

export default ChatComponent;
